/**
 * الحصول على مواقيت الصلاة لتاريخ معين
 */
export function getPrayerTimesForDate(date: Date): PrayerTime | null {
  const dateStr = date.toISOString().split('T')[0];
  return prayerTimesData.find(pt => pt.miladiDate === dateStr) || null;
}

/**
 * الحصول على مواقيت الصلاة لليوم الحالي
 */
export function getTodayPrayerTimes(): PrayerTime | null {
  // للتجربة: نستخدم تاريخ من 2020 إذا كان التاريخ الحالي خارج نطاق البيانات
  const today = new Date();
  let prayerTime = getPrayerTimesForDate(today);
  
  // إذا لم نجد بيانات لليوم الحالي، نستخدم بيانات نفس اليوم والشهر من 2020
  if (!prayerTime) {
    const month = String(today.getMonth() + 1).padStart(2, '0');
    const day = String(today.getDate()).padStart(2, '0');
    const dateStr2020 = `2020-${month}-${day}`;
    prayerTime = prayerTimesData.find(pt => pt.miladiDate === dateStr2020) || null;
  }
  
  return prayerTime;
}

/**
 * الحصول على الصلاة القادمة - محسّن
 * Improved Next Prayer function with stable logic
 */
export function getNextPrayer(prayerTime: PrayerTime): {
  name: string;
  time: string;
  nameAr: string;
  timeRemaining: string;
  isCurrent?: boolean;
} | null {
  if (!prayerTime) return null;

  const now = new Date();
  const currentTime = now.getHours() * 60 + now.getMinutes();
  const currentSeconds = now.getSeconds();

  const prayers = [
    { name: 'Fajr', nameAr: 'الفجر', time: prayerTime.fajr },
    { name: 'Sunrise', nameAr: 'الشروق', time: prayerTime.sunrise },
    { name: 'Dhuhr', nameAr: 'الظهر', time: prayerTime.dhuhr },
    { name: 'Asr', nameAr: 'العصر', time: prayerTime.asr },
    { name: 'Maghrib', nameAr: 'المغرب', time: prayerTime.maghrib },
    { name: 'Isha', nameAr: 'العشاء', time: prayerTime.isha },
  ];

  // البحث عن الصلاة القادمة مع هامش 30 دقيقة للاستقرار
  // هذا يمنع تغيير الصلاة القادمة بسرعة
  const gracePeriod = 30; // دقائق

  for (let i = 0; i < prayers.length; i++) {
    const prayer = prayers[i];
    const [hours, minutes] = prayer.time.split(':').map(Number);
    const prayerTimeMinutes = hours * 60 + minutes;
    
    // حساب الوقت المتبقي بدقة
    const diff = prayerTimeMinutes - currentTime;
    
    // إذا كانت الصلاة في المستقبل (أكثر من 30 دقيقة)
    if (diff > gracePeriod) {
      const hoursRemaining = Math.floor(diff / 60);
      const minutesRemaining = diff % 60;
      const timeRemaining = hoursRemaining > 0 
        ? `${hoursRemaining}س ${minutesRemaining}د`
        : `${minutesRemaining}د`;

      return {
        name: prayer.name,
        nameAr: prayer.nameAr,
        time: prayer.time.substring(0, 5),
        timeRemaining,
        isCurrent: false
      };
    }
    
    // إذا كانت الصلاة قريبة (أقل من 30 دقيقة) أو جارية (أقل من 15 دقيقة)
    if (diff > -15) {
      const isCurrent = diff <= 15 && diff >= -15; // في نطاق 15 دقيقة قبل وبعد
      const absDiff = Math.abs(diff);
      const hoursRemaining = Math.floor(absDiff / 60);
      const minutesRemaining = absDiff % 60;
      
      let timeRemaining: string;
      if (isCurrent) {
        timeRemaining = diff >= 0 ? `${minutesRemaining}د` : 'جارية';
      } else {
        timeRemaining = hoursRemaining > 0 
          ? `${hoursRemaining}س ${minutesRemaining}د`
          : `${minutesRemaining}د`;
      }

      return {
        name: prayer.name,
        nameAr: prayer.nameAr,
        time: prayer.time.substring(0, 5),
        timeRemaining,
        isCurrent
      };
    }
  }

  // إذا انتهى وقت جميع الصلوات، نعرض صلاة الفجر لليوم التالي
  return {
    name: 'Fajr',
    nameAr: 'الفجر',
    time: prayers[0].time.substring(0, 5),
    timeRemaining: 'غداً',
    isCurrent: false
  };
}

/**
 * تحديد حالة الصلاة (قادمة، جارية، انتهت)
 */
export function getPrayerStatus(prayerTime: string): 'upcoming' | 'current' | 'passed' {
  const now = new Date();
  const currentTime = now.getHours() * 60 + now.getMinutes();
  const [hours, minutes] = prayerTime.split(':').map(Number);
  const prayerTimeMinutes = hours * 60 + minutes;

  if (prayerTimeMinutes > currentTime + 30) return 'upcoming';
  if (prayerTimeMinutes > currentTime - 15) return 'current';
  return 'passed';
}

/**
 * أسماء أيام الأسبوع
 */
const arabicDayNames = ['الأحد', 'الاثنين', 'الثلاثاء', 'الأربعاء', 'الخميس', 'الجمعة', 'السبت'];
const englishDayNames = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];

/**
 * أسماء الأشهر الهجرية
 */
const hijriMonths = [
  'محرم', 'صفر', 'ربيع الأول', 'ربيع الثاني', 
  'جمادى الأولى', 'جمادى الثانية', 'رجب', 'شعبان', 
  'رمضان', 'شوال', 'ذو القعدة', 'ذو الحجة'
];

/**
 * تحويل التاريخ الميلادي إلى هجري
 * Gregorian to Hijri conversion
 */
export function convertToHijri(date: Date = new Date()): {
  day: number;
  month: number;
  year: number;
  monthName: string;
  dayName: string;
} {
  // خوارزمية Kuwaiti algorithm
  let day = date.getDate();
  let month = date.getMonth() + 1;
  let year = date.getFullYear();

  // Convert to Julian Day Number
  let a = Math.floor((14 - month) / 12);
  let y = year + 4800 - a;
  let m = month + (12 * a) - 3;
  
  let julianDay = day + Math.floor((153 * m + 2) / 5) + 
                  (365 * y) + Math.floor(y / 4) - 
                  Math.floor(y / 100) + Math.floor(y / 400) - 32045;

  // Convert Julian Day to Hijri
  let l = julianDay - 1948440 + 10632;
  let n = Math.floor((l - 1) / 10631);
  l = l - 10631 * n + 354;
  
  let j = (Math.floor((10985 - l) / 5316)) * 
          (Math.floor((50 * l) / 17719)) + 
          (Math.floor(l / 5670)) * 
          (Math.floor((43 * l) / 15238));
  
  l = l - (Math.floor((30 - j) / 15)) * 
      (Math.floor((17719 * j) / 50)) - 
      (Math.floor(j / 16)) * 
      (Math.floor((15238 * j) / 43)) + 29;
  
  let hijriMonth = Math.floor((24 * l) / 709);
  let hijriDay = l - Math.floor((709 * hijriMonth) / 24);
  let hijriYear = 30 * n + j - 30;

  const dayOfWeek = date.getDay();

  return {
    day: hijriDay,
    month: hijriMonth,
    year: hijriYear,
    monthName: hijriMonths[hijriMonth - 1],
    dayName: arabicDayNames[dayOfWeek]
  };
}

/**
 * تنسيق التاريخ الميلادي بالعربية
 */
export function formatGregorianDateAr(date: Date = new Date()): string {
  const months = [
    'يناير', 'فبراير', 'مارس', 'أبريل', 'مايو', 'يونيو',
    'يوليو', 'أغسطس', 'سبتمبر', 'أكتوبر', 'نوفمبر', 'ديسمبر'
  ];
  
  const day = date.getDate();
  const month = months[date.getMonth()];
  const year = date.getFullYear();
  const dayName = arabicDayNames[date.getDay()];
  
  return `${dayName}، ${day} ${month} ${year}`;
}

/**
 * تنسيق التاريخ الميلادي بالإنجليزية
 */
export function formatGregorianDateEn(date: Date = new Date()): string {
  const months = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];
  
  const day = date.getDate();
  const month = months[date.getMonth()];
  const year = date.getFullYear();
  const dayName = englishDayNames[date.getDay()];
  
  return `${dayName}, ${month} ${day}, ${year}`;
}

